import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";
import BoardPlugin from 'phaser3-rex-plugins/plugins/board-plugin.js';


const PhaserMain = () => {

    let player;
    let cursors;
    let layer;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                preload: preload,
                create: create,
                update: update,

            },
            plugins: {
                scene: [{
                    key: 'rexBoard',
                    plugin: BoardPlugin,
                    mapping: 'rexBoard'
                },
                ]
            }
        }
    }

    function preload() {
        this.load.image('environment', 'assets/environment_forest.png');
        this.load.spritesheet('knight',
            'assets/knight.png',
            {frameWidth: 75, frameHeight: 54}
        );
        this.load.tilemapCSV('map', 'assets/grid.csv');

    }

    function create() {
        this.add.image(480, 220, 'environment');
        let map = this.make.tilemap({key: 'map', tileWidth: 50, tileHeight: 50});
        layer = map.createStaticLayer(0, "null", 0, 0);
        let grid = this.add.grid(480, 370, 900, 400, 50, 50, "black", "0.4");
        player = this.add.sprite(60, 525, 'knight');
        cursors = this.input.keyboard.createCursorKeys();


        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('knight', {start: 0, end: 9}),
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {
        if (cursors.right.isDown) {
            let tile = layer.getTileAtWorldXY(player.x + 10, player.y, true);

            if (tile.index === 2) {
                //  Blocked, we can't move
            } else {
                player.x += 10;
                player.angle = 0;
            }
        }
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
