import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";


const PhaserMain = () => {

    let player;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                preload: preload,
                create: create,
                update: update,
            },
        }
    }

    function preload() {
        this.load.image('environment', 'assets/environment_forest.png');
        this.load.spritesheet('knight',
            'assets/knight.png',
            { frameWidth: 32, frameHeight: 48 }
        );
    }
    function create() {
        this.add.image(480, 220, 'environment');
        let grid = this.add.grid(480, 370, 900, 400, 50, 50,  "black", "0.4");
        player = this.physics.add.sprite(100, 450, 'dude');

        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('dude', { start: 1, end: 10 }),
            frameRate: 10,
            repeat: -1
        });
    }
    function update() {
    }
    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
