import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";
import BoardPlugin from 'phaser3-rex-plugins/plugins/board-plugin.js';


const PhaserMain = () => {

    let player;
    let cursors;
    let platforms;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                key: "main",
                preload: preload,
                create: create,
                update: update,
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: {y: 500},
                    debug: false
                }
            },
        }
    }

    function preload() {
        /*this.load.image("environment", "assets/environment_forest.png");*/
        this.load.spritesheet("adventurer",
            "assets/adventurer.png",
            {frameWidth: 82, frameHeight: 110}
        );
        this.load.image("map1", "assets/map1.png");

    }

    function create() {
        player = this.physics.add.sprite(100, 450, "adventurer");
        player.setCollideWorldBounds(true);
        platforms = this.physics.add.staticGroup();
        platforms.create(50, 10, "map1");

        cursors = this.input.keyboard.createCursorKeys();

        platforms = this.physics.add.staticGroup();



        this.anims.create({
            key: 'left',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 5, end: 6 }),
            frameRate: 10,
            repeat: -1
        });

        this.anims.create({
            key: 'turn',
            frames: [ { key: 'adventurer', frame: 4 } ],
            frameRate: 20
        });

        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 7, end: 8 }),
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {

        if (cursors.left.isDown)
        {
            player.setVelocityX(-160);

            player.anims.play('left', true);
        }
        else if (cursors.right.isDown)
        {
            player.setVelocityX(160);

            player.anims.play('right', true);
        }
        else
        {
            player.setVelocityX(0);

            player.anims.play('turn');
        }

        if (cursors.space.isDown && player.body.touching.down)
        {
            player.setVelocityY(-330);
        }
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
