import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";
import BoardPlugin from 'phaser3-rex-plugins/plugins/board-plugin.js';
import Board from "phaser3-rex-plugins/plugins/board/board/LogicBoard";
import {tilesmap} from "./consts/tilesmap";
import Chess from "phaser3-rex-plugins/plugins/board/chess/ChessData";


const PhaserMain = () => {

    let player;
    let cursors;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                preload: preload,
                create: create,
                update: update,

            },
            plugins: {
                scene: [{
                    key: 'rexBoard',
                    plugin: BoardPlugin,
                    mapping: 'rexBoard'
                },
                ]
            }
        }
    }

    function preload() {
        this.load.image('environment', 'assets/environment_forest.png');
        this.load.spritesheet('knight',
            'assets/knight.png',
            { frameWidth: 75, frameHeight: 54 }
        );
    }
    function create() {
       this.add.image(480, 220, 'environment');
/*
        let grid = this.add.grid(480, 370, 900, 400, 50, 50,  "black", "0.4");*!/
*/
        let map = new Board(this, tilesmap);
        player = new Chess(map, {x: 0, y:0})
        /*player = this.add.sprite(100, 450, 'knight');*/
        cursors = this.input.keyboard.createCursorKeys();


        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('knight', { start: 0, end: 9 }),
            frameRate: 10,
            repeat: -1
        });
    }
    function update() {
     if (cursors.right.isDown)
        {
            player.setVelocityX(160);

            player.anims.play('right', true);
        }
    /* else {
         player.setVelocityX(0);

     }*/
    }

        return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
