import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";
import BoardPlugin from 'phaser3-rex-plugins/plugins/board-plugin.js';


const PhaserMain = () => {

    let player;
    let cursors;
    let layer;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                key: "main",
                preload: preload,
                create: create,
                update: update,
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: {y: 500},
                    debug: false
                }
            },
        }
    }

    function preload() {
        /*this.load.image("environment", "assets/environment_forest.png");*/
    }

    function create() {
        player = this.physics.add.sprite(100, 450, "knight");
        cursors = this.input.keyboard.createCursorKeys();


        this.anims.create({
            key: "right",
            frames: this.anims.generateFrameNumbers("knight", {start: 5, end: 8}),
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
