import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";


const PhaserMain = () => {

    let player;
    let cursors;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            pixelArt: true,
            scene: {
                preload: preload,
                create: create,
                update: update,
            },
        }
    }

    function preload() {
        this.load.image('environment', 'assets/environment_forest.png');
        this.load.spritesheet('knight',
            'assets/knight.png',
            { frameWidth: 75, frameHeight: 54 }
        );
        this.load.tilemapCSV('map', 'assets/tilemaps/csv/grid.csv');

    }
    function create() {
        let map = this.make.tilemap({ key: 'map', tileWidth: 32, tileHeight: 32 });
        let tileset = map.addTilesetImage('environment', null, 32, 32, 1, 2);
        let layer = map.createStaticLayer(0, tileset, 0, 0);

/*
        let grid = this.add.grid(480, 370, 900, 400, 50, 50,  "black", "0.4");
*/
        player = this.add.sprite(100, 450, 'knight');
        cursors = this.input.keyboard.createCursorKeys();


        this.input.keyboard.on('keydown_A', function (event) {

            var tile = layer.getTileAtWorldXY(player.x - 32, player.y, true);

            if (tile.index === 2)
            {
                //  Blocked, we can't move
            }
            else
            {
                player.x -= 32;
                player.angle = 180;
            }

        });

        //  Right
        this.input.keyboard.on('keydown_D', function (event) {

            var tile = layer.getTileAtWorldXY(player.x + 32, player.y, true);

            if (tile.index === 2)
            {
                //  Blocked, we can't move
            }
            else
            {
                player.x += 32;
                player.angle = 0;
            }

        });

        //  Up
        this.input.keyboard.on('keydown_W', function (event) {

            var tile = layer.getTileAtWorldXY(player.x, player.y - 32, true);

            if (tile.index === 2)
            {
                //  Blocked, we can't move
            }
            else
            {
                player.y -= 32;
                player.angle = -90;
            }

        });

        //  Down
        this.input.keyboard.on('keydown_S', function (event) {

            var tile = layer.getTileAtWorldXY(player.x, player.y + 32, true);

            if (tile.index === 2)
            {
                //  Blocked, we can't move
            }
            else
            {
                player.y += 32;
                player.angle = 90;
            }

        });

    }
    function update() {
    }
    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
