import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";


const PhaserMain = () => {

    let player;
    let cursors;
    let map;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                key: "main",
                preload: preload,
                create: create,
                update: update,
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: {y: 500},
                    debug: false
                }
            },
        }
    }

    function preload() {
        this.load.image('background', 'assets/images/background.png');
        this.load.spritesheet("adventurer",
            "assets/images/adventurer.png",
            {frameWidth: 82, frameHeight: 110}
        );
        this.load.spritesheet('tiles', 'assets/tilesets/platformPack_tilesheet.png', {frameWidth: 64, frameHeight: 64});
        this.load.tilemapTiledJSON('map', 'assets/tilemaps/level1.json');
    }

    function create() {
        const backgroundImage = this.add.image(0, 0,'background').setOrigin(0, 0);
        backgroundImage.setScale(2, 0.8);

        const map = this.make.tilemap({ key: 'map' });
        const tileset = map.addTilesetImage( 'tiles');

        const platforms = map.createStaticLayer('Platforms', tileset, 0, 100);
        platforms.setCollisionByExclusion(-1, true);
        cursors = this.input.keyboard.createCursorKeys();
        player = this.physics.add.sprite(800, 30, "adventurer");



        this.anims.create({
            key: 'left',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 5, end: 6 }),
            frameRate: 10,
            repeat: -1
        });

        this.anims.create({
            key: 'turn',
            frames: [ { key: 'adventurer', frame: 4 } ],
            frameRate: 20
        });

        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 7, end: 8 }),
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {

        if (cursors.left.isDown)
        {
            player.setVelocityX(-160);

            player.anims.play('left', true);
        }
        else if (cursors.right.isDown)
        {
            player.setVelocityX(160);

            player.anims.play('right', true);
        }
        else
        {
            player.setVelocityX(0);

            player.anims.play('turn');
        }

        if (cursors.space.isDown && player.body.touching.down)
        {
            player.setVelocityY(-330);
        }
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
