import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";


const PhaserMain = () => {

    let player;
    let cursors;
    let coins;
    let coinLayer;
    let golem1, golem2, golem3, golem4;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                key: "main",
                preload: preload,
                create: create,
                update: update,
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: {y: 500},
                    debug: false
                }
            },
        }
    }

    function preload() {
        this.load.image('background', 'assets/images/background.png');
        this.load.spritesheet("adventurer",
            "assets/images/adventurer.png",
            {frameWidth: 64, frameHeight: 88}
        );
        this.load.image('tiles', 'assets/tilesets/platformPack_tilesheet.png');
        this.load.tilemapTiledJSON('map', 'assets/tilemaps/level1.json');
        this.load.image("coin", "assets/images/coin.png");
        this.load.spritesheet("golemLeft",
            "assets/images/golemLeft.png",
            {frameWidth: 135, frameHeight: 90}
        );
        this.load.spritesheet("golemRight",
            "assets/images/golemRight.png",
            {frameWidth: 135, frameHeight: 90}
        );

    }

    function create() {
        //background
        const backgroundImage = this.add.image(0, 0, 'background').setOrigin(0, 0);
        backgroundImage.setScale(20, 0.8);
        //map
        const map = this.make.tilemap({key: 'map'});
        const tileset = map.addTilesetImage("kenney_simple_platformer", 'tiles');
        const platforms = map.createStaticLayer('Platforms', tileset, 0, 200);
        //coin layer
        coinLayer = map.getObjectLayer('Coins')['objects'];
        map.createStaticLayer("Background", tileset, 0, 200)
        coins = this.physics.add.staticGroup()
        coinLayer.forEach(object => {
            const coin = coins.create(object.x, object.y + 200 - object.height, 'coin').setOrigin(0, 0);
        });
        // map
        platforms.setCollisionByExclusion(-1, true);
        //cursors
        cursors = this.input.keyboard.createCursorKeys();
        //player
        player = this.physics.add.sprite(200, 300, "adventurer");
        player.body.setSize(player.width - 15)
        //golems
        golem1 = this.physics.add.sprite(200, 300, "golemRight");
        golem1.body.setSize(player.width - 15, player.height -10)
        golem1.setVelocityX(-160);

        //physics
        this.physics.add.collider(player, platforms);
        this.physics.add.collider(golem1, platforms);
        this.physics.add.overlap(player, coins, collectCoin, null, this);
        //camera
        this.cameras.main.setBounds(0, 0, backgroundImage.displayWidth, backgroundImage.displayHeight);
        this.cameras.main.startFollow(player);

        //animations
        this.anims.create({
            key: 'left',
            frames: this.anims.generateFrameNumbers('adventurer', {start: 7, end: 8}),
            frameRate: 10,
            repeat: -1
        });

        this.anims.create({
            key: 'stand',
            frames: [{key: 'adventurer', frame: 6}],
            frameRate: 20
        });

        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('adventurer', {start: 9, end: 10}),
            frameRate: 10,
            repeat: -1
        });
        this.anims.create({
            key: 'jump-right',
            frames: [{key: 'adventurer', frame: 3}],
            frameRate: 10,
            repeat: -1
        });
        this.anims.create({
            key: 'jump-left',
            frames: [{key: 'adventurer', frame: 2}],
            frameRate: 10,
            repeat: -1
        });
        this.anims.create({
            key: 'attack-right',
            frames: [{key: 'adventurer', frame: 1}],
            frameRate: 10,
            repeat: -1
        });
        this.anims.create({
            key: 'attack-left',
            frames: [{key: 'adventurer', frame: 0}],
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {

        /*move*/
        if (cursors.left.isDown && !cursors.space.isDown) {
            player.setVelocityX(-160);
            if (player.body.onFloor()) {
                player.anims.play('left', true);
            }
        } else if (cursors.right.isDown && !cursors.space.isDown) {
            player.setVelocityX(160);
            if (player.body.onFloor()) {
                player.anims.play('right', true);
            }
        } else {
            player.setVelocityX(0);
            player.anims.play('stand');
        }
        /*jump*/
        if (cursors.up.isDown && player.body.onFloor()) {
            player.setVelocityY(-330);
            player.anims.play('jump-right', true);
        } else if (!player.body.onFloor()) {
            if (cursors.left.isDown) {
                player.anims.play('jump-left', true);
            } else {
                player.anims.play('jump-right', true);
            }
        }
        /*action*/
        if (cursors.space.isDown && cursors.left.isDown && player.body.onFloor()) {
            player.anims.play('attack-left', true);
        } else if (cursors.space.isDown && player.body.onFloor()) {
            player.anims.play('attack-right', true);
        }

    }

    function collectCoin(player, coin) {
        coin.destroy(coin.x, coin.y);
        return false;
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
