import {IonPhaser} from "@ion-phaser/react";
import React from "react";
import Phaser from "phaser";
import BoardPlugin from 'phaser3-rex-plugins/plugins/board-plugin.js';


const PhaserMain = () => {

    let player;
    let cursors;
    let layer;

    const gameState = {
        initialize: true,
        game: {
            width: 960,
            height: 600,
            type: Phaser.AUTO,
            scene: {
                key: "main",
                preload: preload,
                create: create,
                update: update,
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: {y: 500},
                    debug: false
                }
            },
        }
    }

    function preload() {
        /*this.load.image("environment", "assets/environment_forest.png");*/
        this.load.spritesheet("adventurer",
            "assets/adventurer.png",
            {frameWidth: 80, frameHeight: 110}
        );
    }

    function create() {
        player = this.physics.add.sprite(100, 450, "adventurer");
        player.setBounce(0.35);
        player.setCollideWorldBounds(true);
        cursors = this.input.keyboard.createCursorKeys();


        this.anims.create({
            key: 'left',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 0, end: 3 }),
            frameRate: 10,
            repeat: -1
        });

        this.anims.create({
            key: 'turn',
            frames: [ { key: 'adventurer', frame: 4 } ],
            frameRate: 20
        });

        this.anims.create({
            key: 'right',
            frames: this.anims.generateFrameNumbers('adventurer', { start: 5, end: 8 }),
            frameRate: 10,
            repeat: -1
        });
    }

    function update() {
    }

    return (
        <>
            <IonPhaser game={gameState.game} initialize={gameState.initialize}/>
        </>
    );
}
export default PhaserMain;
